export class Graph {
    constructor() {
        this.adj = new Map();
        this.nodes = [];
        this.edges = [];
        this.v = 1;
    }
    addNode() {
        const node = {id: this.v, color: 'black'}
        this.nodes.push(node);
        this.adj.set(this.v++, {node, neighbors: []});
    }
    addEdge(source, target) {
        this.adj.get(source).neighbors.push(target);
        this.adj.get(target).neighbors.push(source);
    }
    recolor(vertex, color) {
        this.adj.get(vertex).node.color = 'black'
    }
};
