import * as d3 from 'd3';
//if you add event listeners, be sure to remove them when you delete the node or destroy
//the graph
const radius = 7;
export class Graph {
    constructor(svg, centerX, centerY, defaultColor) {
        this.adj = new Map();
        this.nodes = [];
        this.edges = [];
        this.v = 0;
        this.defaultColor = defaultColor
        this.simulation = d3.forceSimulation(this.nodes)
            .force("link", d3.forceLink(this.edges).strength(0))
            .force("collide", d3.forceCollide().radius(30))
            .force("center", d3.forceCenter(centerX, centerY))
            .force("x", d3.forceX().x(centerX))
            .force("y", d3.forceY().y(centerY).strength(0.5));
        this.svg = svg;
        this.circles = this.svg.append('g')
            .classed('node-group', true);
        this.lines = this.svg.append('g')
            .classed('edge-group', true);
        this.lines.lower();
        this.simulation.on("tick", () => {
            this.circles.selectAll(".node")
                .attr('cx', node => node.x)
                .attr('cy', node => node.y);
    //   text
    //     .attr('x', node => node.x)
    //     .attr('y', node => node.y);
            this.lines.selectAll(".edge")
                .attr('x1', edge => edge.source.x)
                .attr('x2', edge => edge.target.x)
                .attr('y1', edge => edge.source.y)
                .attr('y2', edge => edge.target.y);
        });
    }
    updateNodes() {
        this.circles
            .selectAll('.node')
            .data(this.nodes)
            .enter()
            .append('circle')
            .classed('node', true)
            .attr('fill', node => node.color)
            .attr('stroke', 'black')
            .attr('r', radius);
    }
    updateEdges() {
        this.lines
            .selectAll('.edge')
            .data(this.edges)
            .enter()
            .append('line')
            .classed('edge', true)
            // .on('click', handleClick)
            .on('mouseover', (event) => {
                d3.select(event.currentTarget)
                .style('stroke', 'red')
                // .raise();
            })
            .on('mouseout', (event) => {
                d3.select(event.currentTarget)
                .style('stroke', 'gray')
            })
            .lower();
    }
    addNode() {
        const node = {id: this.v, color: this.defaultColor}
        this.nodes.push(node);
        this.adj.set(this.v++, {node, neighbors: []});
        this.simulation.nodes(this.nodes).alpha(0.2).restart();
        this.updateNodes();
    }
    addEdge(source, target) {
        if(source === target || this.adj.get(source).neighbors.includes(target)) return;
        this.adj.get(source).neighbors.push(target);
        this.adj.get(target).neighbors.push(source);
        this.edges.push({source: this.adj.get(source).node, target: this.adj.get(target).node});
        this.simulation.force("link", d3.forceLink(this.edges));
        this.updateEdges();
    }
    recolor(vertex, color) {
        this.adj.get(vertex).node.color = color;
    }
};
