import logo from './logo.svg';
import './App.css';
import * as d3 from 'd3';
import {useEffect, useRef} from 'react';
import {Graph} from './Graph'

//current issues: duplicates are present in the adjacency list (as in, 1: [0, 0, 1])
//                we still want the adjacency to go both ways, since this is an
//                undirected graph
//duplicates are present in the edges list, which is easy (just don't add the edge twice)

//although maybe want to make sure that the edge that
//they're adding, when they're adding it,
//doesn't already exist... but can do that check in the
//adjacency list in O(deg) time

//maybe have pitches deepen as the recursion stack grows
const innerHeight = window.innerHeight;
const innerWidth = window.innerWidth;
const centerX = innerWidth / 2;
const centerY = innerHeight / 2;
const radius = 5;
const showLabels = false;

const boundsCheck = (value, bound) => {
  if(value < radius) return radius + 10;
  if(value > bound - radius) return bound - 10;
  return value;
}

const handleClick = (event) => {
  if(event.target.tagName === 'circle') console.log('node clicked');
  else if(event.target.tagName === 'line') console.log('edge clicked');
  else console.log('background clicked')
}

const App = () => {
  const containerRef = useRef(null);
  useEffect(() => {
    const graph = new Graph(centerX, centerY, '#45d097');
    for(let i = 0; i < 20; ++i) graph.addNode();
    for(let i = 0; i < 20; ++i) {
      for(let j = 0; j < 20; ++j) {
        graph.addEdge(i, j);
      }
    }
    const svg = d3.select(containerRef.current)
        .append('svg')
        .attr('width', innerWidth)
        .attr('height', innerHeight)
        .on('click', handleClick);

    const lines = svg
      .selectAll('line')
      .data(graph.edges)
      .enter()
      .append('line')
      .classed('edge', true)
      .on('click', handleClick)
      .on('mouseover', (event) => {
        d3.select(event.currentTarget)
        .style('stroke', 'red');
      })
      .on('mouseout', (event) => {
        d3.select(event.currentTarget)
        .style('stroke', 'gray')
      });

    const circles = svg
      .selectAll('circle')
      .data(graph.nodes)
      .enter()
      .append('circle')
      .classed('node', true)
      .attr('fill', node => node.color)
      .attr('r', radius)
      .on('click', handleClick); //consider making this print out node id when clicked just to
      //know you know that i can do it you know you know
      /*
        .on('click', node => {
          console.log(`Node ${node.id} clicked`);
        });
      */

    const text = svg
      .selectAll('text')
      .data(graph.nodes)
      .enter()
      .append('text')
      .attr('text-anchor', 'middle')
      .attr('alignment-baseline', 'middle')
      .style('pointer-events', 'none')
      .style('user-select', 'none')
      .text(node => showLabels ? node.id : "");

    graph.simulation.on("tick", () => {
      circles
        .attr('cx', node => boundsCheck(node.x, innerWidth))
        .attr('cy', node => boundsCheck(node.y, innerHeight));
      text
        .attr('x', node => node.x)
        .attr('y', node => node.y);
      lines
        .attr('x1', edge => boundsCheck(edge.source.x, innerWidth))
        .attr('x2', edge => boundsCheck(edge.target.x, innerWidth))
        .attr('y1', edge => boundsCheck(edge.source.y, innerHeight))
        .attr('y2', edge => boundsCheck(edge.target.y, innerHeight));
    });
    graph.simulation.alpha(1).restart();
    console.log(graph.adj);
    console.log(graph.nodes);
    console.log(graph.edges);
  }, []);

  return <div ref={containerRef} />
}

export default App;
