import logo from './logo.svg';
import './App.css';
import * as d3 from 'd3';
import {useEffect, useRef} from 'react';
import {Graph} from './Graph'

const innerHeight = window.innerHeight;
const innerWidth = window.innerWidth;
const centerX = innerWidth / 2;
const centerY = innerHeight / 2;

const App = () => {
  const containerRef = useRef(null);
  useEffect(() => {
    const graph = new Graph(centerX, centerY, '#45d097');
    for(let i = 0; i < 400; ++i) graph.addNode();
    for(let i = 0; i < 200; ++i) {
      graph.addEdge(i, i * 2); 
      graph.addEdge(i, i * 2 + 1);
    }
    const svg = d3.select(containerRef.current)
        .append('svg')
        .attr('width', innerWidth)
        .attr('height', innerHeight);

    const lines = svg
      .selectAll('line')
      .data(graph.edges)
      .enter()
      .append('line')
      .classed('edge', true);

    const circles = svg
      .selectAll('circle')
      .data(graph.nodes)
      .enter()
      .append('circle')
      .classed('node', true)
      .attr('fill', node => node.color)
      .attr('r', 5);

    const text = svg
      .selectAll('text')
      .data(graph.nodes)
      .enter()
      .append('text')
      .attr('text-anchor', 'middle')
      .attr('alignment-baseline', 'middle')
      .style('pointer-events', 'none')
      .style('user-select', 'none')
      .text(node => node.id);

    graph.simulation.on("tick", () => {
      circles
        .attr('cx', node => (node.x > innerWidth - 5) ? node.x - radius : node.x)
        .attr('cy', node => node.y);
      text
        .attr('x', node => node.x)
        .attr('y', node => node.y);
      lines
        .attr('x1', edge => edge.source.x)
        .attr('x2', edge => edge.target.x)
        .attr('y1', edge => edge.source.y)
        .attr('y2', edge => edge.target.y);
    });
    graph.simulation.alpha(1).restart();
    console.log(graph.adj);
    console.log(graph.nodes);
    console.log(graph.edges);
  }, []);

  return <div ref={containerRef} />
}

export default App;
